body {
  margin: 0;
  font-family: system-ui, sans-serif;
  background: #020617;
  color: #e5e7eb;
}

h1, h2, h3 {
  color: #f9fafb;
}

.btn-primary {
  background: linear-gradient(90deg, #8b5cf6, #ec4899);
  color: #f9fafb;
  padding: 0.5rem 1.5rem;
  border-radius: 9999px;
  border: none;
}

#dashboard-title {
  letter-spacing: 0.08em;
  text-transform: uppercase;
}

.dashboard-shell {
  display: grid;
  grid-template-columns: 2.2fr 1.1fr;
  gap: 2rem;
}

.compiler-layout {
  display: grid;
  grid-template-columns: 1.2fr 1fr;
  gap: 1.5rem;
}

@media (max-width: 768px) {
  .dashboard-shell,
  .compiler-layout {
    grid-template-columns: 1fr;
  }
}

.btn-primary {
  transition: transform 150ms ease, box-shadow 150ms ease;
}

.btn-primary:hover,
.btn-primary:focus-visible {
  transform: translateY(-2px);
  box-shadow: 0 10px 30px rgba(15,23,42,0.7);
}

input:focus-visible {
  outline: 2px solid #38bdf8;
  outline-offset: 2px;
}

h1 {
  color: #f97316;
  font-size: 2.25rem;
}

p {
  line-height: 1.6;
  color: #e5e7eb;
}

/* type selector */
button { padding: 0.5rem 1.5rem; }

/* class selector */
.button-primary { background: #8b5cf6; color: white; }

/* id selector */
#enroll-btn { border-radius: 9999px; }

body {
  font-family: system-ui, sans-serif;
  color: #e5e7eb;
}

.dashboard-title {
  color: #f9fafb; /* overrides body color for this element only */
}

.muted {
  color: #9ca3af !important; /* hard override (use sparingly) */
}

*, *::before, *::after {
  box-sizing: border-box;
}

.card {
  width: 320px;
  padding: 1.25rem;
  border-radius: 1rem;
  border: 1px solid #1f2937;
  margin: 1rem;
}

.page-shell {
  background: radial-gradient(circle at top, #1d4ed8 0, #020617 55%, #000 100%);
  color: #e5e7eb;
}

.enroll-badge {
  background-color: #f97316;
  color: #111827;
}

body {
  font-family: system-ui, -apple-system, BlinkMacSystemFont, sans-serif;
  font-size: 16px;
  line-height: 1.5;
}

h1 {
  font-size: 2.5rem;
  letter-spacing: 0.08em;
  text-transform: uppercase;
}

.top-bar {
  position: sticky;
  top: 0;
  z-index: 40;
  display: flex;
  align-items: center;
}

.notification-pill {
  position: absolute;
  top: -0.3rem;
  right: -0.3rem;
}

.course-row {
  display: flex;
  gap: 1.5rem;
  overflow-x: auto;
}

.course-row > .course-card {
  flex: 0 0 260px;
}

.dashboard-grid {
  display: grid;
  grid-template-columns: 2.2fr 1.1fr;
  gap: 2rem;
}

.compiler-grid {
  display: grid;
  grid-template-columns: 1.2fr 1fr;
  gap: 1.5rem;
}

@media (max-width: 768px) {
  .dashboard-grid,
  .compiler-grid {
    grid-template-columns: 1fr;
  }

  .side-menu {
    display: none;
  }
}

.btn-primary {
  background: linear-gradient(90deg, #8b5cf6, #ec4899);
  color: #f9fafb;
  transition: transform 150ms ease, box-shadow 150ms ease;
}

.btn-primary:hover,
.btn-primary:focus-visible {
  transform: translateY(-2px);
  box-shadow: 0 10px 30px rgba(15,23,42,0.7);
}

.course-card::before {
  content: "";
  position: absolute;
  inset: 0;
  border-radius: 1.25rem;
  border: 1px solid rgba(148,163,184,0.2);
}

.pulse-dot {
  width: 10px;
  height: 10px;
  border-radius: 9999px;
  background: #22c55e;
  animation: pulse 1.5s infinite;
}

@keyframes pulse {
  0% { transform: scale(1); opacity: 1; }
  100% { transform: scale(2); opacity: 0; }
}

.glass-card {
  border-radius: 1.5rem;
  border: 1px solid rgba(148,163,184,0.28);
  box-shadow: 0 24px 60px rgba(15,23,42,0.9);
  backdrop-filter: blur(24px);
}

:root {
  --bg-dark: #020617;
  --accent: #8b5cf6;
  --accent-soft: #4c1d95;
}

body {
  background-color: var(--bg-dark);
}

.badge-accent {
  background: var(--accent-soft);
  color: #f9fafb;
}

/* utilities */
.text-xs { font-size: 0.75rem; }
.text-muted { color: #9ca3af; }

/* component */
.compiler-panel {
  background: #020617;
  border-radius: 1.25rem;
  border: 1px solid #1f2937;
  padding: 1.25rem;
}

let username = "Arjun";
const maxScore = 100;
let isEnrolled = true;

console.log(username, maxScore, isEnrolled);

let level = "intermediate";

if (level === "beginner") {
  console.log("Start with HTML and CSS.");
} else if (level === "intermediate") {
  console.log("Time to practice JavaScript projects.");
} else {
  console.log("Explore advanced topics and frameworks.");
}

const courses = ["HTML", "CSS", "JavaScript"];

for (let i = 0; i < courses.length; i++) {
  console.log(i, courses[i]);
}

for (const course of courses) {
  console.log("Course:", course);
}

function greet(name) {
  return `Welcome back, ${name}!`;
}

const sum = (a, b) => a + b;

console.log(greet("Arjun"));
console.log(sum(5, 7));

const scores = [72, 88, 95];

scores.push(100);
const highScores = scores.filter(score => score >= 90);

console.log(scores);
console.log(highScores);

const course = {
  title: "Blockchain Basics",
  level: "Intermediate",
  enrolled: 1200,
  describe() {
    return `${this.title} (${this.level}) – ${this.enrolled} learners`;
  }
};

console.log(course.title);
console.log(course.describe());

const titleEl = document.querySelector("#dashboard-title");

titleEl.textContent = "Learn Peak – JavaScript Lab";
titleEl.classList.add("text-accent");

const enrollBtn = document.querySelector("#enroll-btn");

enrollBtn.addEventListener("click", () => {
  console.log("User clicked Enroll Now");
});

const json = '{"title":"AI - Machine Learning","level":"Advanced"}';

const course = JSON.parse(json);
console.log(course.title);

const payload = JSON.stringify(course);
console.log(payload);

async function loadCourses() {
  const res = await fetch("/api/courses");
  const data = await res.json();
  console.log("Courses:", data);
}

loadCourses().catch(err => console.error(err));

// stats.js
export function average(arr) {
  return arr.reduce((sum, n) => sum + n, 0) / arr.length;
}

// dashboard.js
import { average } from "./stats.js";

console.log(average([70, 80, 90]));

function parseLevel(raw) {
  try {
    const obj = JSON.parse(raw);
    return obj.level;
  } catch (err) {
    console.error("Invalid JSON", err);
    return "unknown";
  }
}

console.log(parseLevel('{"level":"Beginner"}'));
console.log(parseLevel("not json"));
